import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [contact, setContact] = useState('');
  const [otp, setOtp] = useState('');
  const [sent, setSent] = useState(false);
  const navigate = useNavigate();

  const sendOTP = async () => {
    await fetch('http://localhost:5000/api/send-otp', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({ contact }),
    });
    setSent(true);
  };

  const verifyOTP = async () => {
    const res = await fetch('http://localhost:5000/api/verify-otp', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({ contact, otp }),
    });
    if (res.ok) {
      navigate('/upload');
    } else {
      alert("Invalid OTP");
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h1 className="text-xl font-bold mb-4">Login with OTP</h1>
      <input
        type="text"
        placeholder="Mobile or Email"
        value={contact}
        onChange={(e) => setContact(e.target.value)}
        className="border p-2 w-full mb-2"
      />
      {sent ? (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            className="border p-2 w-full mb-2"
          />
          <button onClick={verifyOTP} className="bg-blue-600 text-white px-4 py-2 rounded">Verify OTP</button>
        </>
      ) : (
        <button onClick={sendOTP} className="bg-green-600 text-white px-4 py-2 rounded">Send OTP</button>
      )}
    </div>
  );
}
