import React, { useState } from 'react';

export default function Upload() {
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState('');

  const uploadFile = async () => {
    const formData = new FormData();
    formData.append('file', file);

    const res = await fetch('http://localhost:5000/api/upload', {
      method: 'POST',
      body: formData,
    });

    const data = await res.json();
    if (data.success) {
      setMsg('Uploaded: ' + data.filename);
    } else {
      setMsg('Upload failed.');
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h1 className="text-xl font-bold mb-4">Upload File</h1>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} className="mb-2" />
      <button onClick={uploadFile} className="bg-blue-600 text-white px-4 py-2 rounded">Upload</button>
      {msg && <p className="mt-2 text-green-700">{msg}</p>}
    </div>
  );
}
